import React, { useState } from "react";
import { Handle } from "reactflow";
// Outlined Message icon (chat bubble)
const messageIcon = (
  <svg width="22" height="22" viewBox="0 0 512 512" xmlns="http://www.w3.org/2000/svg" style={{ display: 'block', backgroundColor: 'transparent' }}>
    <path d="M256 32C114.6 32 .0272 125.1 .0272 240c0 47.63 19.91 91.25 52.91 126.2c-14.88 39.5-45.87 72.88-46.37 73.25c-6.625 7-8.375 17.25-4.625 26C5.818 474.2 14.38 480 24 480c61.5 0 109.1-25.75 139.1-46.25C191.1 442.8 223.3 448 256 448c141.4 0 255.1-93.13 255.1-208S397.4 32 256 32zM256.1 400c-26.75 0-53.12-4.125-78.38-12.12l-22.75-7.125l-19.5 13.75c-14.25 10.12-33.88 21.38-57.5 29c7.375-12.12 14.37-25.75 19.88-40.25l10.62-28l-20.62-21.87C69.82 314.1 48.07 282.2 48.07 240c0-88.25 93.25-160 208-160s208 71.75 208 160S370.8 400 256.1 400z" fill="#25D366"/>
  </svg>
);

// Outlined WhatsApp icon
const whatsappIcon = (
  <svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ display: 'block' }}>
    <circle cx="11" cy="11" r="11" fill="#C6F9D6" />
    <path d="M14.7 13.1c-.13-.07-.75-.38-.88-.44-.13-.07-.19-.07-.25.07-.07.13-.32.44-.38.5-.07.07-.13.07-.25.07-.13-.07-.5-.19-.94-.56-.38-.31-.63-.75-.69-.88-.07-.13 0-.19.07-.25.07-.07.13-.13.19-.19.07-.07.07-.13.13-.19.07-.07.07-.13.13-.19.07-.07.07-.13.07-.19 0-.07 0-.13-.07-.19-.07-.07-.32-.75-.44-1-.13-.25-.25-.19-.38-.19-.13 0-.19 0-.31.13-.13.13-.44.44-.44 1.06 0 .63.5 1.25.69 1.44.25.25 1.06.81 1.44.94.38.13.69.13.94.07.25-.07.81-.31.88-.56.07-.25.07-.5.07-.56 0-.07-.07-.07-.13-.13z" stroke="#25D366" strokeWidth="1.2" fill="none" />
  </svg>
);

const TextNode = ({ data, selected }) => {
  const [message, setMessage] = useState(data.message || "New message");
  return (
    <div
      style={{
        borderRadius: 24,
        boxShadow: "0 4px 16px rgba(0,0,0,0.10)",
        background: "#fff",
        minWidth: 340,
        maxWidth: 380,
        minHeight: 120,
        border: selected ? "2px solid #25D366" : "none",
        position: "relative",
        fontFamily: "Inter, sans-serif",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Header with left message icon, label, and WhatsApp icon */}
      <div
        style={{
          background: "#C6F9D6",
          borderTopLeftRadius: 24,
          borderTopRightRadius: 24,
          height: 44,
          display: "flex",
          alignItems: "center",
          padding: "0 12px",
          gap: 0,
          position: "relative",
        }}
      >
        {/* Left message icon */}
        <div style={{ width: 28, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>{messageIcon}</div>
        {/* Label */}
        <span style={{ fontWeight: 600, fontSize: 18, color: "#222", flex: 1, textAlign: 'left', marginLeft: 8 }}>Send Message</span>
        {/* WhatsApp icon right-aligned */}
        <div style={{ width: 28, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>{whatsappIcon}</div>
      </div>
      {/* Handles */}
      <Handle type="target" position="left" style={{ top: 56, background: "#222" }} />
      <Handle type="source" position="right" style={{ top: 56, background: "#222" }} />
      {/* Message text */}
      <div style={{ padding: "24px 32px 24px 32px", fontSize: 28, color: "#222", textAlign: "left" }}>
        <input
          type="text"
          value={message}
          onChange={e => setMessage(e.target.value)}
          style={{
            border: "none",
            outline: "none",
            background: "transparent",
            fontSize: 28,
            color: "#222",
            width: "100%",
            fontWeight: 400,
            padding: 0,
            margin: 0,
          }}
        />
      </div>
    </div>
  );
};

export default TextNode;
