import React from 'react'
import { useFlowState } from '../../state/flowContext'
import NodesPanel from './NodesPanel'
import SettingsPanel from './SettingsPanel'

export default function SidePanel() {
  const { selectedNodeId } = useFlowState()

  // When a node is selected, replace the NodesPanel with SettingsPanel (per requirements)
  return (
    <div style={{ display: 'flex', width: '100%' }}>
      <aside className="left-panel">
        {selectedNodeId ? <SettingsPanel /> : <NodesPanel />}
      </aside>
      <aside className="side-panel">
        {/* Right side can be used for inspector or additional info; currently empty */}
        <div style={{ padding: 8 }}> </div>
      </aside>
    </div>
  )
}
