import React, { useState } from "react";
import { Handle } from "react-flow-renderer";
// Message icon (left bubble)
const messageIcon = (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ display: 'block' }}>
    <circle cx="14" cy="14" r="14" fill="#25D366" />
    <path d="M19.5 11.5c0-2.2-2.2-4-5-4s-5 1.8-5 4c0 1.2.8 2.3 2.1 3-.1.3-.3.7-.6 1.1-.1.2.1.5.4.4 1.1-.3 2.1-.7 3.1-.7 2.8 0 5-1.8 5-4z" fill="#fff" />
  </svg>
);

// WhatsApp icon (right)
const whatsappIcon = (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ display: 'block' }}>
    <circle cx="14" cy="14" r="14" fill="#25D366" />
    <path d="M20.7 17.1c-.3-.2-1.5-.8-1.7-.9-.2-.1-.4-.2-.6.1-.2.3-.6.9-.8 1-.2.2-.3.2-.5.1-.3-.1-1-.4-1.8-1.1-.8-.7-1.2-1.5-1.3-1.7-.1-.2 0-.4.2-.5.2-.2.3-.4.4-.5.1-.2.2-.3.3-.4.1-.2.2-.3.3-.4.1-.2.2-.3.2-.4 0-.2 0-.3-.2-.4-.2-.2-.7-1.5-.9-2-.2-.5-.5-.4-.7-.4-.2 0-.4 0-.7.3-.3.3-.9 1-1 2.1 0 1.2.9 2.4 1.3 2.8.5.5 2 1.6 2.7 1.8.7.2 1.3.2 1.8.1.5-.1 1.5-.6 1.6-1.1.1-.5.2-1 .2-1.1 0-.1-.1-.2-.3-.3z" fill="#fff" />
  </svg>
);

const TextNode = ({ data, selected }) => {
  const [message, setMessage] = useState(data.message || "New message");
  return (
    <div
      style={{
        borderRadius: 24,
        boxShadow: "0 4px 16px rgba(0,0,0,0.10)",
        background: "#fff",
        minWidth: 340,
        maxWidth: 380,
        minHeight: 120,
        border: selected ? "2px solid #25D366" : "none",
        position: "relative",
        fontFamily: "Inter, sans-serif",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Header with left message icon, label, and WhatsApp icon */}
      <div
        style={{
          background: "#C6F9D6",
          borderTopLeftRadius: 24,
          borderTopRightRadius: 24,
          height: 56,
          display: "flex",
          alignItems: "center",
          padding: "0 24px",
          gap: 16,
          position: "relative",
        }}
      >
        {/* Left message icon */}
        <div style={{ marginRight: 12 }}>{messageIcon}</div>
        {/* Label */}
        <span style={{ fontWeight: 600, fontSize: 18, color: "#222", marginRight: 16 }}>Send Message</span>
        {/* WhatsApp icon right-aligned */}
        <div style={{ marginLeft: "auto" }}>{whatsappIcon}</div>
      </div>
      {/* Handles */}
      <Handle type="target" position="left" style={{ top: 56, background: "#222" }} />
      <Handle type="source" position="right" style={{ top: 56, background: "#222" }} />
      {/* Message text */}
      <div style={{ padding: "24px 32px 24px 32px", fontSize: 28, color: "#222", textAlign: "left" }}>
        <input
          type="text"
          value={message}
          onChange={e => setMessage(e.target.value)}
          style={{
            border: "none",
            outline: "none",
            background: "transparent",
            fontSize: 28,
            color: "#222",
            width: "100%",
            fontWeight: 400,
            padding: 0,
            margin: 0,
          }}
        />
      </div>
    </div>
  );
};

export default TextNode;
