import React from 'react'
import { useFlowState } from '../../state/flowContext'
import NodesPanel from './NodesPanel'
import SettingsPanel from './SettingsPanel'

export default function SidePanel({ rightPanelOnly }) {
  const { selectedNodeId } = useFlowState()

  // If rightPanelOnly, always show NodesPanel (for right-side layout)
  if (rightPanelOnly) {
    return <NodesPanel />
  }

  // Otherwise, show SettingsPanel if node selected, else NodesPanel
  return selectedNodeId ? <SettingsPanel /> : <NodesPanel />
}
