import React, { useCallback, useRef } from 'react'
import ReactFlow, { addEdge, useNodesState, useEdgesState } from 'reactflow'
import 'reactflow/dist/style.css'
import { nanoid } from 'nanoid'
import { getDefaultNode } from '../../utils/nodeSchemas'
import TextNode from './nodes/TextNode'
import { useFlowState, useFlowDispatch } from '../../state/flowContext'

const nodeTypes = { textNode: TextNode }

export default function FlowCanvas() {
  const { nodes: ctxNodes, edges: ctxEdges } = useFlowState()
  const dispatch = useFlowDispatch()

  // keep local react-flow states synced with global context for simplicity
  const [nodes, setNodes, onNodesChange] = useNodesState(ctxNodes)
  const [edges, setEdges, onEdgesChange] = useEdgesState(ctxEdges)

  // when react-flow nodes/edges change, update global state
  const syncToContext = useCallback(
    (ns, es) => {
      // avoid dispatching if nothing changed to prevent loops
      try {
        const sameNodes = JSON.stringify(ctxNodes) === JSON.stringify(ns)
        const sameEdges = JSON.stringify(ctxEdges) === JSON.stringify(es)
        if (sameNodes && sameEdges) return
      } catch (err) {
        // fallthrough to dispatch if stringify fails
      }

      dispatch({ type: 'LOAD_FLOW', nodes: ns, edges: es })
    },
    [dispatch, ctxNodes, ctxEdges],
  )

  const onConnect = useCallback(
    params => {
      // enforce single outbound per source handle
      const exists = edges.some(e => e.source === params.source && e.sourceHandle === params.sourceHandle)
      if (exists) {
        alert('Each source handle may only have one outbound connection')
        return
      }
      const newEdge = addEdge({ ...params, id: 'e' + nanoid() }, edges)
      setEdges(newEdge)
      syncToContext(nodes, newEdge)
    },
    [edges, setEdges, syncToContext, nodes],
  )

  const onNodeClick = useCallback((event, node) => {
    // set selected node in global state so SettingsPanel shows
    dispatch({ type: 'SET_SELECTED', id: node.id })
  }, [dispatch])

  const reactFlowWrapper = useRef(null)

  const onDrop = useCallback(
    event => {
      event.preventDefault()
      const reactFlowType = event.dataTransfer.getData('application/reactflow')
      if (!reactFlowType) return

      const bounds = reactFlowWrapper.current?.getBoundingClientRect() || { left: 0, top: 0 }
      const position = { x: event.clientX - bounds.left, y: event.clientY - bounds.top }
      const id = nanoid()
      const newNode = getDefaultNode(reactFlowType, id, position)
      setNodes(nds => nds.concat(newNode))
      // sync using resulting nodes array (best effort: compute locally)
      syncToContext([...nodes, newNode], edges)
    },
    [edges, nodes, setNodes, syncToContext],
  )

  const onDragOver = useCallback(event => event.preventDefault(), [])

  // when local nodes/edges change via reactflow hooks, sync to context
  React.useEffect(() => syncToContext(nodes, edges), [nodes, edges, syncToContext])

  // keep local react-flow state in sync when context changes (e.g. editing in SettingsPanel)
  React.useEffect(() => {
    try {
      if (JSON.stringify(ctxNodes) !== JSON.stringify(nodes)) setNodes(ctxNodes)
    } catch (err) {
      setNodes(ctxNodes)
    }
  }, [ctxNodes, nodes, setNodes])

  React.useEffect(() => {
    try {
      if (JSON.stringify(ctxEdges) !== JSON.stringify(edges)) setEdges(ctxEdges)
    } catch (err) {
      setEdges(ctxEdges)
    }
  }, [ctxEdges, edges, setEdges])

  return (
    <div ref={reactFlowWrapper} style={{ width: '100%', height: '100%' }} onDrop={onDrop} onDragOver={onDragOver}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onNodeClick={onNodeClick}
        fitView
      />
    </div>
  )
}
