import React from 'react'
import { Handle, Position } from 'reactflow'

// WhatsApp SVG icon
const WhatsAppIcon = (
  <svg width="20" height="20" viewBox="0 0 32 32" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ verticalAlign: 'middle' }}>
    <circle cx="16" cy="16" r="16" fill="#25D366" />
    <path d="M22.5 18.5c-.3-.2-1.7-.8-2-1-.3-.2-.5-.2-.7.1-.2.3-.8 1-.9 1.1-.2.2-.3.2-.6.1-.3-.2-1.2-.4-2.3-1.3-.8-.7-1.3-1.5-1.5-1.8-.2-.3 0-.5.1-.7.1-.1.2-.3.3-.4.1-.1.1-.2.2-.3.1-.2.1-.3 0-.5-.1-.2-.7-1.7-.9-2.3-.2-.6-.4-.5-.6-.5h-.5c-.2 0-.5.1-.7.3-.2.2-1.2 1.2-1.2 2.9 0 1.7 1.2 3.3 1.4 3.5.2.2 2.3 3.6 5.7 3.6 1.1 0 2.2-.4 2.5-.8.3-.4.7-1.1.8-1.3.1-.2.1-.4-.1-.6z" fill="#fff" />
  </svg>
)

export default function TextNode({ data }) {
  const invalid = data?.__invalid
  return (
    <div
      className={`node-card ${invalid ? 'invalid-node' : ''}`}
      style={{
        borderRadius: 12,
        boxShadow: '0 4px 16px rgba(0,0,0,0.08)',
        background: '#fff',
        minWidth: 220,
        maxWidth: 260,
        padding: 0,
        position: 'relative',
      }}
    >
      {/* Handles */}
      <Handle type="target" position={Position.Left} style={{ top: '50%', background: '#222' }} />
      <Handle type="source" position={Position.Right} id="out" style={{ top: '50%', background: '#222' }} />

      {/* Header */}
      <div
        style={{
          background: '#b7f5d0',
          borderTopLeftRadius: 12,
          borderTopRightRadius: 12,
          padding: '8px 12px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
        }}
      >
        <span style={{ fontWeight: 700, fontSize: '1.1rem', color: '#222', display: 'flex', alignItems: 'center', gap: 6 }}>
          <span style={{ marginRight: 6, verticalAlign: 'middle' }}>Send Message</span>
          {WhatsAppIcon}
        </span>
      </div>
      {/* Message text */}
      <div
        style={{
          padding: '12px 14px',
          fontSize: '1rem',
          fontWeight: 400,
          color: '#222',
          whiteSpace: 'pre-line',
          minHeight: 32,
        }}
      >
        {data?.text}
      </div>
    </div>
  )
}
