import React from 'react'
import { useFlowState } from '../../state/flowContext'
import { validateBeforeSave } from '../../utils/validators'
import { exportFlow } from '../../utils/export'

export default function TopBar() {
  const state = useFlowState()

  function onSave() {
    const res = validateBeforeSave(state.nodes, state.edges)
    if (!res.ok) {
      alert('Cannot save flow. Nodes with empty targets: ' + res.nodes.map(n => n.label).join(', '))
      // TODO: highlight nodes
      return
    }

    const payload = exportFlow(state.nodes, state.edges)
    // trigger download
    const blob = new Blob([payload], { type: 'application/json' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = 'flow.json'
    a.click()
    URL.revokeObjectURL(url)
    alert('Flow exported')
  }

  return (
    <header className="topbar">
      <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
        <strong>Bitespeed Flow</strong>
      </div>
      <div>
        <button className="btn btn-primary" onClick={onSave}>
          Save Changes
        </button>
      </div>
    </header>
  )
}
